import browser
import sys

class StdOutStream:
    def write(self, data):
        browser.self.print(str(data))

    def flush(self):
        pass


class StdErrStream:
    def write(self, data=''):
        browser.self.printErr(str(data))

    def flush(self):
        pass


sys.stdout = StdOutStream()
sys.stderr = StdErrStream()
